import React from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import {
  Typography,
  IconButton,
  Grid,
  List,
  ListItem,
} from '@material-ui/core';
import FacebookIcon from '@material-ui/icons/Facebook';
import TwitterIcon from '@material-ui/icons/Twitter';
import InstagramIcon from '@material-ui/icons/Instagram';
import PinterestIcon from '@material-ui/icons/Pinterest';
import logo from '../../../../assets/images/logo.svg';
import { Image } from 'components/atoms';

import ScriptTag from 'react-script-tag';
const useStyles = makeStyles(theme => ({
  root: {
    padding: theme.spacing(6, 0),
    [theme.breakpoints.up('md')]: {
      padding: theme.spacing(12, 0),
    },
    background: theme.palette.background.footer,
  },
  footerContainer: {
    maxWidth: theme.layout.contentWidth,
    width: '100%',
    margin: '0 auto',
    padding: theme.spacing(0, 2),
    [theme.breakpoints.up('sm')]: {
      padding: theme.spacing(0, 8),
    },
  },
  logoContainerItem: {
    paddingTop: 0,
  },
  logoContainer: {
    width: 120,
    height: 32,
  },
  logoImage: {
    width: '100%',
    height: '100%',
  },
  groupTitle: {
    textTransform: 'uppercase',
    color: theme.palette.primary.dark,
    marginBottom: theme.spacing(1),
  },
  socialIcon: {
    padding: 0,
    marginRight: theme.spacing(1),
    color: 'rgba(255,255,255,.6)',
    '&:hover': {
      background: 'transparent',
    },
    '&:last-child': {
      marginRight: 0,
    },
  },
  icon: {
    fontSize: 24,
  },
  menuListContainer: {
    padding: '0 !important',
  },
  menu: {
    display: 'flex',
  },
  menuItem: {
    margin: theme.spacing(2),
    '&:last-child': {
      marginBottom: 0,
    },
  },
  menuGroupItem: {
    paddingTop: 0,
    paddingBottom: theme.spacing(1 / 2),
    '&:last-child': {
      paddingBottom: 0,
    },
  },
  menuGroupTitle: {
    textTransform: 'uppercase',
    color: 'white',
  },
  divider: {
    width: '100%',
  },
  navLink: {
    color: 'rgba(255,255,255,.6)',
  },
}));

const Footer = props => {
  const { pages, className, ...rest } = props;

  const classes = useStyles();
  const packages = pages.packages;
  const supportedPages = pages.pages;
  const account = pages.account;
  const aboutus=pages.aboutus;

  const MenuGroup = props => {
    const { item } = props;
    return (
      <List disablePadding className={classes.menuItem}>
        <ListItem disableGutters className={classes.menuGroupItem}>
          <Typography variant="body2" className={classes.menuGroupTitle}>
            {item.groupTitle}
          </Typography>
        </ListItem>
        {item.pages.map((page, i) => (
          <ListItem disableGutters key={i} className={classes.menuGroupItem}>
            <Typography
              variant="body2"
              component={'a'}
              href={page.href}
              className={clsx(classes.navLink, 'submenu-item')}
            >
              {page.title}
            </Typography>
          </ListItem>
        ))}
      </List>
    );
  };

  const LandingPages = () => {
    const { web } = packages.children;
   // const { services, apps, web } = packages.children;
    return (
      <div className={classes.menu}>
        {/* <div>
          <MenuGroup item={services} />
          <MenuGroup item={apps} />
        </div> */}
        <div>
          <MenuGroup item={web} />
        </div>
      </div>
    );
  };

  const SupportedPages = () => {
    const {

      helpCenter,
      // company,
      contact,
      // blog,
      // portfolio,
    } = supportedPages.children;
    // const {
    //   career,
    //   helpCenter,
    //   company,
    //   contact,
    //   blog,
    //   portfolio,
    // } = supportedPages.children;
    return (
      <div className={classes.menu}>
        <div>
          {/* <MenuGroup item={career} /> */}
          <MenuGroup item={helpCenter} />
        </div>
        <div>
          {/* <MenuGroup item={company} /> */}
          <MenuGroup item={contact} />
        </div>
        {/* <div>
          <MenuGroup item={blog} />
          <MenuGroup item={portfolio} />
        </div> */}
      </div>
    );
  };

  const AccountPages = () => {
    const { settings, signup, signin, password, error } = account.children;
    return (
      <div className={classes.menu}>
        <div>
          <MenuGroup item={settings} />

        </div>
        {/* <div>
          <MenuGroup item={signin} />
          <MenuGroup item={password} />
           <MenuGroup item={signup} />
          <MenuGroup item={error} />
        </div> */}
      </div>
    );
  };

  const AboutPages = () => {

      const { company } = aboutus.children;
      return (
        <div className={classes.menu}>

            <MenuGroup item={company} />


        </div>
      );
    };
  return (

    <div {...rest} className={clsx(classes.root, className)}>
      <div className={classes.footerContainer}>

        <Grid container spacing={4}>

          <Grid item xs={12} md={2}>
            <List disablePadding>
              <ListItem disableGutters className={classes.logoContainerItem}>
                <div className={classes.logoContainer}>
                  <a href="/" title="Primo099">
                    <Image
                      className={classes.logoImage}
                      src={logo}
                      alt="Primo099"
                      lazy={false}
                    />
                  </a>
                </div>
              </ListItem>

              <ListItem disableGutters>
                <IconButton className={classes.socialIcon}>
                  <FacebookIcon className={classes.icon} />
                </IconButton>
                <IconButton className={classes.socialIcon}>
                  <InstagramIcon className={classes.icon}   />

                </IconButton>
                <IconButton className={classes.socialIcon}>
                  <TwitterIcon className={classes.icon} />
                </IconButton>
                <IconButton className={classes.socialIcon}>
                  <PinterestIcon className={classes.icon} />
                </IconButton>
              </ListItem>

            </List>
          </Grid>
          <Grid item xs={12} md={10} className={classes.menuListContainer}>
            <Grid container spacing={0}>
            <Grid item>
                <AboutPages/>
              </Grid>
              <Grid item>
                <LandingPages />
              </Grid>
              <Grid item>
                <SupportedPages />
              </Grid>
              <Grid item>
                <AccountPages />



              </Grid>
            </Grid>
          </Grid>
        </Grid>
      </div>
    </div>

  );
};

Footer.propTypes = {
  className: PropTypes.string,
  pages: PropTypes.object.isRequired,
};

export default Footer;
